import {
  Divider,
  FormControl,
  FormControlLabel,
  Grid,
  Radio,
  RadioGroup,
  Typography,
} from "@mui/material";
import React, { useEffect, useState } from "react";
import PlaceIcon from "@mui/icons-material/Place";
import CalendarTodayIcon from "@mui/icons-material/CalendarToday";
import { MenuCard } from "./MenuCard";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import {
  getRestaurantsCategoryAction,
  getRestaurnatsByIdAction,
} from "../../State/Restaurant/Action";
import { Restaurant } from "@mui/icons-material";

const categories = [
  "pizza",
  "biryani",
  "burger",
  "sandwich",
  "salad",
  "soup",
  "chicken",
  "rice",
];
const FoodType = [
  {
    label: "all",
    value: "all",
  },
  {
    label: "vegetarian only",
    value: "vegetarian",
  },
  {
    label: "non-vegetarian only",
    value: "non-vegetarian",
  },
  {
    label: "Seasonal",
    value: "seasonal",
  },
];
const menu = [1, 1, 1, 1, 1];
export const RestaurantDetails = () => {
  const [foodType, setFoodType] = useState("all");
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const jwt = localStorage.getItem("jwt");
  const { auth, restaurant } = useSelector((store) => store);
  const { id } = useParams();

  useEffect(() => {
    if (id) {
      dispatch(getRestaurnatsByIdAction({ jwt, restaurantId: id }));
    }
  }, [dispatch, jwt, id]);

  const handleFilter = (e) => {
    setFoodType(e.target.value);
    console.log(e.target.value, e.target.name);
  };

  const { restaurants } = restaurant || {}; // Safely destructure restaurants

  if (!restaurants) {
    return <div>Loading restaurant details...</div>;
  }

  return (
    <div className="px-5 lg:px-20">
      <section>
        <h3 className="text-gray-500 py-2 mt-10">
          Home /india/Indian Fast food/{id}
        </h3>
        <Grid container spacing={2}>
          <Grid item xs={12} lg={6}>
            <img
              src="https://cdn.pixabay.com/photo/2018/03/08/18/13/chair-3209341_640.jpg"
              alt=""
              className="w-full h-[40vh] object-cover"
            />
          </Grid>
          {/* Add more images as needed */}
        </Grid>
        <div className="pt-3 pb-5">
          <h1 className="text-4xl font-semibold">{restaurants.name}</h1>
          <span className="text-gray-500 mt-2">
            {restaurants.description || "No description available."}
          </span>
          <div className="space-y-3 pb-5">
            <p className="text-gray-500 flex items-center gap-3">
              <PlaceIcon />
              <span>{restaurants.address || "Address not available."}</span>
            </p>
            <p className="text-gray-500 flex items-center gap-3">
              <CalendarTodayIcon />
              <span>
                {restaurants.openingHours || "Opening hours not available."}
              </span>
            </p>
          </div>
        </div>
      </section>
      {/* Filters and menu section */}
      <Divider />
      <section className="pt-[2rem] lg:flex relative">
        {/* Filter Sidebar */}
        <div className="space-y-10 lg:w-[20%] filter">
          <div className="box space-y-5 lg:sticky top-28 p-5 shadow-md">
            <Typography>Food Type</Typography>
            <FormControl className="py-10 space-y-5" component={"fieldset"}>
              <RadioGroup
                onChange={handleFilter}
                name="food_type"
                value={foodType}
              >
                {FoodType.map((item) => (
                  <FormControlLabel
                    key={item.value}
                    value={item.value}
                    control={<Radio />}
                    label={item.label}
                  />
                ))}
              </RadioGroup>
            </FormControl>
          </div>
        </div>
        {/* Menu Section */}
        <div className="space-y-5 lg:w-[80%] lg:pl-10">
          {menu.map((_, index) => (
            <MenuCard key={index} />
          ))}
        </div>
      </section>
    </div>
  );
};
