import * as ActionType from "./ActionType";

const intialState = {
  loading: false,
  orders: [],
  error: null,
};
export const orderReducer = (state = intialState, action) => {
  switch (action.type) {
    case ActionType.GET_USERS_ORDER_REQUEST:
      return { ...state, loading: true, error: null };
    case ActionType.GET_USERS_ORDER_SUCCESS:
      return { ...state, loading: false, orders: payload };
    case ActionType.GET_USERS_ORDER_FAILURE:
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};
