import React from "react";

import {
  Accordion,
  AccordionDetails,
  AccordionSummary,
  Button,
  Checkbox,
  FormControlLabel,
  FormGroup,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
const demo = [
  {
    category: "Nuts &  Seeds",
    ingredients: ["Almonds", "cachew", "Pistachios"],
  },
  { category: "Protin", ingredients: ["Protin", "protin sake"] },
];
export const MenuCard = (item) => {
  const handleCheckBox = (value) => {
    console.log("value");
  };
  return (
    <>
      <Accordion>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="pane11-content"
          id="pane11-header"
        >
          <div className="lg:flex items-center justify-between">
            <div className="lg:flex items-center lg:gap-4">
              <img
                alt=""
                className="w-[7rem] h-[7rem] object-cover"
                src="https://cdn.pixabay.com/photo/2024/04/18/19/04/lock-8704819_640.jpg"
              />
              <div className="space-y-1 lg:space-y-5 lg:max-w-2xl">
                <p className="font-semibold text-xl">Burger</p>
                <p>$49</p>
                <p className="text-gray-400">Nice Food</p>
              </div>
            </div>
          </div>
        </AccordionSummary>
        <AccordionDetails>
          <form>
            <div className="flex gap-5 flex-wrap">
              {demo.map((item) => (
                <div>
                  <p>{item.category}</p>
                  <FormGroup>
                    {item.ingredients.map((item) => (
                      <FormControlLabel
                        control={
                          <Checkbox
                            onChange={() => {
                              handleCheckBox(item);
                            }}
                          />
                        }
                        label={item}
                      ></FormControlLabel>
                    ))}
                  </FormGroup>
                </div>
              ))}
            </div>
            <div className="pt-6">
              <Button variant="contained" type="submit" disabled={false}>
                {true ? "AddtoCard" : "out of Stock"}
              </Button>
            </div>
          </form>
        </AccordionDetails>
      </Accordion>
    </>
  );
};
