import * as ActionType from "./ActionType";

const intialState = {
  loading: false,
  order: [],
  error: null,
  notification: [],
};
export const orderReducer = (state = intialState, action) => {
  switch (action.type) {
    case ActionType.GET_USERS_ORDER_SUCCESS:
      return {
        ...state,
        loading: false,
        order: action.payload,
      };
    case ActionType.GET_USERS_ORDER_REQUEST:
      return {
        ...state,
        loading: true,
        error: null,
      };
    case ActionType.GET_USERS_ORDER_FAIL:
      return {
        ...state,
        loading: false,
        error: action.payload,
      };
    case ActionType.CLEAR_ERRORS:
      return {
        ...state,
        error: null,
      };
    case ActionType.ADD_ORDER_REQUEST:
      return {
        ...state,
        loading: true,
        error: null,
      };
    case ActionType.ADD_ORDER_SUCCESS:
      return {
        ...state,
        loading: false,
        order: [...state.order, action.payload],
        notification: [...state.notification, action.payload],
      };

    default:
      return state;
  }
};
