import React from "react";
import {
  Accordion,
  AccordionDetails,
  AccordionSummary,
  Button,
  Checkbox,
  FormControlLabel,
  FormGroup,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { CategorizeIngredient } from "../utils/CategorizeIngredient";

const demo = [
  {
    category: "Nuts & Seeds",
    ingredients: ["Almonds", "Cashew", "Pistachios"],
  },
  { category: "Protein", ingredients: ["Protein", "Protein Shake"] },
];

export const MenuCard = ({ item }) => {
  const handleCheckBox = (value) => {
    console.log("value", value); // Log the value correctly
  };

  return (
    <>
      <Accordion>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="pane11-content"
          id="pane11-header"
        >
          <div className="lg:flex items-center justify-between">
            <div className="lg:flex items-center lg:gap-4">
              <img
                alt=""
                className="w-[7rem] h-[7rem] object-cover"
                src={item.images[0]}
              />
              <div className="space-y-1 lg:space-y-5 lg:max-w-2xl">
                <p className="font-semibold text-xl">{item.name}</p>
                <p>${item.price}</p>
                <p className="text-gray-400">{item.description}</p>
              </div>
            </div>
          </div>
        </AccordionSummary>
        <AccordionDetails>
          <form>
            <div className="flex gap-5 flex-wrap">
              {Object.keys(CategorizeIngredient(item.ingredientsItems)).map(
                (ingredientCategory) => (
                  <div key={ingredientCategory}>
                    {" "}
                    {/* Add unique key for categories */}
                    <p>{ingredientCategory}</p>
                    <FormGroup>
                      {CategorizeIngredient(item.ingredientsItems)[
                        ingredientCategory
                      ].map((ingredient, index) => (
                        <FormControlLabel
                          key={`${ingredientCategory}-${ingredient}`} // Ensure unique key
                          control={
                            <Checkbox
                              onChange={() => {
                                handleCheckBox(ingredient);
                              }}
                            />
                          }
                          label={ingredient} // Assuming the item itself is the name
                        />
                      ))}
                    </FormGroup>
                  </div>
                )
              )}
            </div>
            <div className="pt-6">
              <Button variant="contained" type="submit" disabled={false}>
                {true ? "Add to Cart" : "Out of Stock"}
              </Button>
            </div>
          </form>
        </AccordionDetails>
      </Accordion>
    </>
  );
};
