import React, { useState, useMemo } from "react";

import {
  Accordion,
  AccordionDetails,
  AccordionSummary,
  Button,
  Checkbox,
  FormControlLabel,
  FormGroup,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";

import { CategorizeIngredient } from "../utils/CategorizeIngredient";
import { useDispatch } from "react-redux";
import { addItemToCart } from "../../State/Cart/Action";

const MenuCard = ({ item }) => {
  const dispatch = useDispatch();
  const [selectedIngredients, setSelectedIngredients] = useState([]);

  const handleCheckBox = (itemName) => {
    console.log("Toggling ingredient:", itemName);
    if (selectedIngredients.includes(itemName)) {
      setSelectedIngredients(
        selectedIngredients.filter((ing) => ing !== itemName)
      );
    } else {
      setSelectedIngredients([...selectedIngredients, itemName]);
    }
  };

  const handleAddtoCart = (e) => {
    e.preventDefault();
    console.log("Add to cart clicked");
    const reqData = {
      token: localStorage.getItem("jwt"),
      cartItem: {
        foodId: item.id,
        quantity: 1,
        ingredients: selectedIngredients,
      },
    };
    console.log("Request data: ", reqData);
    dispatch(addItemToCart(reqData));
  };

  // Deep comparison using useMemo with JSON.stringify
  const categorizedIngredients = useMemo(() => {
    console.log("CategorizeIngredient computation triggered");
    return CategorizeIngredient(item.ingredientsItems);
  }, [JSON.stringify(item.ingredientsItems)]); // Depend on a deep string comparison of ingredients.

  console.log("Categorized Ingredients:", categorizedIngredients);

  return (
    <>
      <Accordion>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="pane11-content"
          id="pane11-header"
        >
          <div className="lg:flex items-center justify-between">
            <div className="lg:flex items-center lg:gap-4">
              <img
                alt=""
                className="w-[7rem] h-[7rem] object-cover"
                src={item.images[0]}
              />
              <div className="space-y-1 lg:space-y-5 lg:max-w-2xl">
                <p className="font-semibold text-xl">{item.name}</p>
                <p>${item.price}</p>
                <p className="text-gray-400">{item.description}</p>
              </div>
            </div>
          </div>
        </AccordionSummary>
        <AccordionDetails>
          <form>
            <div className="flex gap-5 flex-wrap">
              {Object.keys(categorizedIngredients).map((ingredientCategory) => (
                <div key={ingredientCategory}>
                  <p>{ingredientCategory}</p>
                  <FormGroup>
                    {categorizedIngredients[ingredientCategory].map(
                      (ingredient) => (
                        <FormControlLabel
                          key={ingredient.id}
                          control={
                            <Checkbox
                              checked={selectedIngredients.includes(
                                ingredient.name
                              )}
                              onChange={() => handleCheckBox(ingredient.name)}
                            />
                          }
                          label={ingredient.name}
                        />
                      )
                    )}
                  </FormGroup>
                </div>
              ))}
            </div>
            <div className="pt-6">
              <Button
                onClick={handleAddtoCart}
                variant="contained"
                type="submit"
                disabled={false}
              >
                Add to Cart
              </Button>
            </div>
          </form>
        </AccordionDetails>
      </Accordion>
    </>
  );
};

// Wrap component with React.memo for preventing unnecessary re-renders
export default React.memo(MenuCard);
