export const CategorizeIngredient = (ingredientsItems) => {
  if (!Array.isArray(ingredientsItems)) {
    console.error("Invalid or missing ingredients:", ingredientsItems);
    return {}; // Return an empty object as a fallback
  }

  return ingredientsItems.reduce((acc, ingredient) => {
    const category = ingredient.category || "Uncategorized"; // Default fallback to "Uncategorized"

    if (!acc[category]) {
      acc[category] = [];
    }

    acc[category].push(ingredient); // Add ingredient to the appropriate category

    return acc;
  }, {});
};
