import {
  CREATE_MENU_ITEM_FAILURE,
  CREATE_MENU_ITEM_REQUEST,
  CREATE_MENU_ITEM_SUCCESS,
  DELETE_MENU_ITEM_FAILURE,
  DELETE_MENU_ITEM_REQUEST,
  DELETE_MENU_ITEM_SUCCESS,
  GET_MENU_ITEM_BY_RESTAURANT_ID_FAILURE,
  GET_MENU_ITEM_BY_RESTAURANT_ID_REQUEST,
  GET_MENU_ITEM_BY_RESTAURANT_ID_SUCCESS,
  SEARCH_MENU_ITEM_FAILURE,
  SEARCH_MENU_ITEM_REQUEST,
  SEARCH_MENU_ITEM_SUCCESS,
  UPDATE_MENU_ITEM_AVALIBLITY_FAILURE,
  UPDATE_MENU_ITEM_AVALIBLITY_REQUEST,
  UPDATE_MENU_ITEM_AVALIBLITY_SUCCESS,
} from "./ActionType";

export const createMenuItem = ({ menu, jwt }) => {
  return async (dispatch) => {
    ({ type: CREATE_MENU_ITEM_REQUEST });
    try {
      const { data } = await Api.post("/api/admin/food", menu, {
        Headers: {
          Authorization: `Bearar ${jwt}`,
        },
      });
      console.log("create menu", data);
      dispatch({ type: CREATE_MENU_ITEM_SUCCESS, payload: data });
    } catch (error) {
      console.log("error from  create menu", error);
      dispatch({ type: CREATE_MENU_ITEM_FAILURE, patload: error });
    }
  };
};

export const getMenuItemByRestaurantIdAction = (reqData) => {
  return async (dispatch) => {
    ({ type: GET_MENU_ITEM_BY_RESTAURANT_ID_REQUEST });
    try {
      const { data } = await Api.get(
        `/api/food/restaurant/${reqData / restaurantId}?vegetarian=${
          reqData.vegetarian
        }&nonVeg=${reqData.nonveg}&seasonal=${reqData.seasonal}&food_category=${
          reqData.foodCategory
        }`,

        {
          Headers: {
            Authorization: `Bearar ${reqData.jwt}`,
          },
        }
      );
      console.log("create menu", data);
      dispatch({ type: GET_MENU_ITEM_BY_RESTAURANT_ID_SUCCESS, payload: data });
    } catch (error) {
      console.log("error from  create menu", error);
      dispatch({
        type: GET_MENU_ITEM_BY_RESTAURANT_ID_FAILURE,
        patload: error,
      });
    }
  };
};

export const searchMenuItem = ({ keyword, jwt }) => {
  return async (dispatch) => {
    ({ type: SEARCH_MENU_ITEM_REQUEST });
    try {
      const { data } = await Api.get(`/api/food/search?name/${keyword}`, menu, {
        Headers: {
          Authorization: `Bearar ${jwt}`,
        },
      });
      console.log("data ...........", data);
      dispatch({ type: SEARCH_MENU_ITEM_SUCCESS, payload: data });
    } catch (error) {
      console.log("error from  search menu", error);
      dispatch({ type: SEARCH_MENU_ITEM_FAILURE, patload: error });
    }
  };
};

