// store.js
import { applyMiddleware, combineReducers, legacy_createStore } from "redux";
import { authReducer } from "./Authentication/Reducer";
import { thunk } from "redux-thunk"; // Corrected import for thunk middleware
import { restaurantsReducer } from "./Restaurant/Reducer";
import { menuReducer } from "./Menu/Reducer";
import { cartReducer } from "./Cart/Reducer";
import { orderReducer } from "./Order/Reducer";
import { ingredientReducer } from "./Ingredients/Reducer";

// Corrected variable name from rooteReducer to rootReducer
const rootReducer = combineReducers({
  auth: authReducer,
  restaurants: restaurantsReducer,
  menu: menuReducer,
  cart: cartReducer,
  order: orderReducer,
  restaurantOrder: restaurantsReducer, // This seems redundant as it points to restaurantsReducer
  ingredients: ingredientReducer,
});

// Create the store using legacy_createStore, applying thunk middleware
export const store = legacy_createStore(rootReducer, applyMiddleware(thunk));
