import React from "react";

import {
  Accordion,
  AccordionDetails,
  AccordionSummary,
  Button,
  Checkbox,
  FormControlLabel,
  FormGroup,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { CatgorizeIngredient } from "../utils/CategorizeIngredient";
const demo = [
  {
    category: "Nuts &  Seeds",
    ingredients: ["Almonds", "cachew", "Pistachios"],
  },
  { category: "Protin", ingredients: ["Protin", "protin sake"] },
];
export const MenuCard = ({ item }) => {
  // Handle the checkbox for ingredients
  const handleCheckBox = (ingredient) => {
    console.log("Ingredient checked:", ingredient);
  };

  return (
    <Accordion>
      <AccordionSummary expandIcon={<ExpandMoreIcon />}>
        <div className="lg:flex items-center justify-between">
          <img
            src={item.images[0]}
            alt={item.name}
            className="w-[7rem] h-[7rem] object-cover"
          />
          <div className="space-y-1 lg:space-y-5 lg:max-w-2xl">
            <p className="font-semibold text-xl">{item.name}</p>
            <p>${item.price}</p>
            <p className="text-gray-400">{item.description}</p>
          </div>
        </div>
      </AccordionSummary>
      <AccordionDetails>
        <form>
          <div className="flex gap-5 flex-wrap">
            {CategorizeIngredient(item.ingredientsItems).map(
              (category, index) => (
                <div key={index}>
                  <p className="font-semibold">{category.name}</p>
                  <FormGroup>
                    {category.items.map((ingredient) => (
                      <FormControlLabel
                        key={ingredient.id}
                        control={
                          <Checkbox
                            onChange={() => handleCheckBox(ingredient)}
                          />
                        }
                        label={ingredient.name}
                      />
                    ))}
                  </FormGroup>
                </div>
              )
            )}
          </div>
          <Button variant="contained" type="submit" disabled={!item.available}>
            {item.available ? "Add to Cart" : "Out of Stock"}
          </Button>
        </form>
      </AccordionDetails>
    </Accordion>
  );
};
