import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { updateCartItem, removeCartItem } from "./path-to-your-actions";
import { IconButton, Chip } from "@mui/material/IconButton";
import RemoveCircleOutlineIcon from "@mui/icons-material/RemoveCircleOutline";
import AddCircleOutlineIcon from "@mui/icons-material/AddCircleOutline";

export const CartItem = ({ item }) => {
  const { auth } = useSelector((store) => store);
  const dispatch = useDispatch();
  const jwt = localStorage.getItem("jwt");

  console.log("id", item.id);

  const handleUpdateCartItem = (value) => {
    if (!item.id) {
      console.error("Cart item update failed: ID is missing.");
      return;
    }

    if (value === -1 && item.quantity === 1) {
      handleRemovedCartItem();
      return;
    }

    const data = { cartItemId: item.id, quantity: item.quantity + value };
    dispatch(updateCartItem({ data, jwt })); // Ensure the JWT token is included
  };

  const handleRemovedCartItem = () => {
    dispatch(removeCartItem({ cartItemId: item.id, auth: auth.jwt || jwt }));
  };

  return (
    <div className="px-5">
      <div className="lg:flex items-center lg:space-x-5">
        <div>
          <img
            src={item.food.images[0]}
            alt={item.food.name}
            className="w-[5rem] h-[5rem] object-cover"
          />
        </div>
        <div className="flex items-center justify-between lg:w-[70%]">
          <div className="space-y-1 lg:space-y-3 w-full">
            <p>{item.food.name}</p>
            <div className="flex justify-between items-center">
              <div className="flex items-center space-x-1">
                <IconButton onClick={() => handleUpdateCartItem(-1)}>
                  <RemoveCircleOutlineIcon />
                </IconButton>
                <div className="w-5 text-xs flex items-center justify-center">
                  {item.quantity}
                </div>
                <IconButton onClick={() => handleUpdateCartItem(1)}>
                  <AddCircleOutlineIcon />
                </IconButton>
              </div>
            </div>
          </div>
          <p>{item.totalPrice}</p>
        </div>
      </div>
      <div className="pt-3 space-x-3">
        {item.ingredients.map((ingredient, index) => (
          <Chip key={index} label={ingredient} />
        ))}
      </div>
    </div>
  );
};
