import React from "react";
import { Chip, IconButton } from "@mui/material";
import RemoveCircleOutlineIcon from "@mui/icons-material/RemoveCircleOutline";
import AddCircleOutlineIcon from "@mui/icons-material/AddCircleOutline";
import { useDispatch, useSelector } from "react-redux";
import { updateCartItem, removeCartItem } from "../../State/Cart/Action";

const IngredientChips = ({ ingredients }) => (
  <div className="pt-3 space-x-3">
    {ingredients?.map((ingredient, index) => (
      <Chip label={ingredient} key={index} />
    ))}
  </div>
);

export const CartItem = ({ item }) => {
  const { auth } = useSelector((state) => state);
  const dispatch = useDispatch();
  const jwt = auth.jwt || localStorage.getItem("jwt");

  console.log(
    "Updated Cart State in Redux:",
    useSelector((state) => state.cart)
  );

  const handleUpdateCartItem = (change) => {
    if (!item.id) {
      console.error("Cart item update failed: ID is missing.");
      alert("Unable to update the cart item. Please try again.");
      return;
    }

    const newQuantity = item.quantity + change;
    if (newQuantity < 1) {
      handleRemovedCartItem();
      return;
    }

    const data = { cartItemId: item.id, quantity: newQuantity };
    dispatch(updateCartItem({ data, jwt }));
  };

  const handleRemovedCartItem = () => {
    if (!item.id) {
      console.error("Cart item removal failed: ID is missing.");
      return;
    }
    dispatch(removeCartItem({ cartItemId: item.id, auth: jwt }));
  };

  const formattedPrice = new Intl.NumberFormat("en-US", {
    style: "currency",
    currency: "USD",
  }).format(item.totalPrice);

  return (
    <div className="px-5">
      <div className="lg:flex items-center lg:space-x-5">
        <img
          src={item.food.images?.[0] || "placeholder.jpg"}
          alt={item.food.name || "Food image"}
          className="w-[5rem] h-[5rem] object-cover"
        />

        <div className="flex items-center justify-between lg:w-[70%]">
          <div className="space-y-1 lg:space-y-3 w-full">
            <p>{item.food.name}</p>
            <div className="flex items-center">
              <IconButton
                onClick={() => handleUpdateCartItem(-1)}
                disabled={item.quantity <= 0}
              >
                <RemoveCircleOutlineIcon />
              </IconButton>
              <div className="w-5 text-xs flex items-center justify-center">
                {item.quantity}
              </div>
              <IconButton onClick={() => handleUpdateCartItem(1)}>
                <AddCircleOutlineIcon />
              </IconButton>
            </div>
          </div>
          <p>{formattedPrice}</p>
        </div>
      </div>
      <IngredientChips ingredients={item.ingredients} />
    </div>
  );
};
