const initialState = {
  isOpen: false,
  items: [],
};

const menuReducer = (state = initialState, action) => {
  // Note the default value
  switch (action.type) {
    case "TOGGLE_MENU":
      return { ...state, isOpen: !state.isOpen };
    // ... other cases
    default:
      return state; // Return the current state for unknown actions
  }
};

export default menuReducer;
